SELECT B.nombres[1]||' '||B.nombres[2] AS nombres, B.apellidos[1]||' '||B.apellidos[2] AS apellidos, A.fecha_registro::date, count(*) FROM ambulancia_sch.ambu_soat_se_siniestro_alt A INNER JOIN usuario_sis B ON A.id_usuario=B.id_usuario WHERE A.estado=0 AND A.fecha_registro> cast(date_trunc('month', current_date) as date) AND A.fecha_registro< cast(current_date as date) GROUP BY A.id_usuario, nombres, apellidos, A.fecha_registro::date

--SELECT B.nombres[1]||' '||B.nombres[2] AS nombres, B.apellidos[1]||' '||B.apellidos[2] AS apellidos, A.fecha_registro::date, count(*) FROM ambulancia_sch.ambu_soat_se_siniestro_alt A INNER JOIN usuario_sis B ON A.id_usuario=B.id_usuario WHERE A.estado=0 AND A.fecha_registro> '2021-11-01' AND A.fecha_registro< cast(current_date as date) GROUP BY A.id_usuario, nombres, apellidos, A.fecha_registro::date