SELECT A.id_reclamante, A.razon_social, A.total, coalesce(B.trabajado,0), A.total - coalesce(B.trabajado,0) as pendiente FROM(SELECT id_reclamante, razon_social, count(*) AS total FROM maos_sch.maos_soat_se_siniestro WHERE fecha_registro>=cast(date_trunc('month', current_date) as date) GROUP BY id_reclamante, razon_social)A LEFT JOIN (SELECT id_reclamante, razon_social, count(*) AS trabajado FROM maos_sch.maos_soat_se_siniestro WHERE estado=0 AND fecha_registro>=cast(date_trunc('month', current_date) as date) AND fecha_registro<cast(current_date as date) GROUP BY id_reclamante, razon_social)B ON A.id_reclamante=B.id_reclamante AND A.razon_social=B.razon_social

--SELECT A.id_reclamante, A.razon_social, A.total, coalesce(B.trabajado,0), A.total - coalesce(B.trabajado,0) as pendiente FROM(SELECT id_reclamante, razon_social, count(*) AS total FROM maos_sch.maos_soat_se_siniestro WHERE fecha_registro>='2021-11-01' GROUP BY id_reclamante, razon_social)A LEFT JOIN (SELECT id_reclamante, razon_social, count(*) AS trabajado FROM maos_sch.maos_soat_se_siniestro WHERE estado=0 AND fecha_registro>='2021-11-01' AND fecha_registro<cast(current_date as date) GROUP BY id_reclamante, razon_social)B ON A.id_reclamante=B.id_reclamante AND A.razon_social=B.razon_social


--SELECT A.id_reclamante, A.razon_social, A.total, B.trabajado FROM(SELECT id_reclamante, razon_social, count(*) AS total FROM maos_sch.maos_soat_se_siniestro WHERE fecha_registro>='2021-05-01' GROUP BY id_reclamante, razon_social)A LEFT JOIN (SELECT id_reclamante, razon_social, count(*) AS trabajado FROM maos_sch.maos_soat_se_siniestro WHERE estado=0 AND fecha_registro>='2021-05-01' AND fecha_registro<'2021-09-26' GROUP BY id_reclamante, razon_social)B ON A.id_reclamante=B.id_reclamante AND A.razon_social=B.razon_social
