SELECT A.id_usuario, B.nombres[1]||' '||B.nombres[2] AS nombres, B.apellidos[1]||' '||B.apellidos[2] AS apellidos, B.correo, count(*) FROM maos_sch.maos_soat_se_siniestro_usuario A INNER JOIN usuario_sis B ON A.id_usuario=B.id_usuario WHERE (A.a_siniestro, A.nro_siniestro, A.nro_cuenta) IN (SELECT a_siniestro, nro_siniestro, nro_cuenta FROM maos_sch.maos_soat_se_siniestro WHERE estado=1) GROUP BY A.id_usuario, B.nombres[1]||' '||B.nombres[2], B.apellidos[1]||' '||B.apellidos[2], B.correo
