SELECT B.nombres[1]||' '||B.nombres[2]||' '||B.apellidos[1]||' '||B.apellidos[2] AS nombre, B.correo, count(*) AS siniestros, C.sum AS item_maos FROM "maos_sch"."maos_soat_se_siniestro" A INNER JOIN usuario_sis B ON A.id_usuario=B.id_usuario INNER JOIN (SELECT id_usuario, sum(count) FROM(SELECT A.nro_siniestro, A.a_siniestro, A.nro_cuenta, B.id_usuario, count(*) FROM "maos_sch"."maos_soat_se_siniestro_proveedor_item" A INNER JOIN "maos_sch"."maos_soat_se_siniestro" B ON A.nro_siniestro = B.nro_siniestro AND A.a_siniestro = B.a_siniestro AND A.nro_cuenta=B.nro_cuenta WHERE B.fecha_registro>='2021-11-01' AND B.fecha_registro<'2021-12-01' GROUP BY A.nro_siniestro, A.a_siniestro, A.nro_cuenta, B.id_usuario)Z GROUP BY id_usuario)C ON A.id_usuario=C.id_usuario WHERE A.estado=0 AND A.fecha_registro>='2021-11-01' AND A.fecha_registro<'2021-12-01' GROUP BY  B.nombres[1]||' '||B.nombres[2]||' '||apellidos[1]||' '||B.apellidos[2], B.correo, C.sum ORDER BY siniestros DESC
