SET lc_time_names = 'es_ES';
SELECT `id_usuario`, `cod_ciudad_usuario`, concat(`id_usuario`,'-',`cod_ciudad_usuario`) AS usuario,concat(monthname(min(`fecha_mov`)),' ',date_format(min(`fecha_mov`),'%Y')) AS Mes, date_format(min(`fecha_mov`),'%d/%m/%Y') AS Fecha_aceptacion, date_format(min(`fecha_mov`),'%H:%i:%S') AS Hora_aceptacion, min(`fecha_mov`) FROM `log_movimientos` WHERE `tipo_mov`="Aceptacion de Terminos" AND `descripcion`="Credenciales correctas, se inicia sesión con aceptación de credenciales" AND `fecha_mov`>'2020-12-09 08:16:28' GROUP BY `id_usuario`, `cod_ciudad_usuario`